 */

import msdyn365Commerce, { IThemeRegistration } from '@msdyn365-commerce/core-internal';

const TEXT_DIRECTION_RTL = 'rtl';
const TEXT_DIRECTION_LTR = 'ltr';

export const getTheme = (isDebug: boolean, themeName: string | undefined, textDirection: string | undefined = undefined): string[] => {
    const themeRegistration: IThemeRegistration = msdyn365Commerce.themes;
    if (!themeName || !themeRegistration || themeName === 'None' || themeName === '' || !themeRegistration.themes) {
        return [];
    }

    if (textDirection === TEXT_DIRECTION_RTL) {
        const rtlTheme = `${themeName}-${TEXT_DIRECTION_RTL}`;
        if (themeRegistration.themes[rtlTheme]) {
            themeName = rtlTheme;
        }
    } else if (textDirection === TEXT_DIRECTION_LTR) {
        const ltrTheme = `${themeName}-${TEXT_DIRECTION_LTR}`;
        if (themeRegistration.themes[ltrTheme]) {
            themeName = ltrTheme;
        }
    }

    const theme = themeRegistration.themes[themeName];

    return theme ? [isDebug ? theme.dev : theme.prod] : [];
};
