import { HttpException } from './HttpException';

/**
 * HTTP error for 302 redirects
 */
export class HttpRedirectException extends HttpException {
    public static Name: string = 'HttpRedirectException';
    public location: string;

    constructor(location: string) {
        super(302);
        this.location = location;
        this.name = HttpRedirectException.Name;
    }
}
