import { IRequestContext } from '../../interfaces';
import { isAuthoringEditEnabled } from '../helper';

const EDITABLE_FIELD_CLASS = 'ms-editable-field';

export const getEditableFieldClassName = (disabled?: boolean, className?: string, requestContext?: IRequestContext) => {
    if (!!disabled) {
        return isAuthoringEditEnabled(requestContext) ? `${EDITABLE_FIELD_CLASS} ${className || ''}` : className;
    } else {
        return isAuthoringEditEnabled(requestContext) ? `${EDITABLE_FIELD_CLASS} enabled ${className || ''}` : className;
    }
};
