import { AppCache, ICache } from '@msdyn365-commerce/cache-internal';
import { IActionInput } from '@msdyn365-commerce/core-internal';

/**
 * Method to indicate if the action should look in app cache for data
 * @param input ActionInput
 */
export const shouldReadFromAppCache = (input: IActionInput, appCache: ICache | null): boolean => {
    return !!appCache && input && input.dataCacheType !== undefined && input.dataCacheType() !== 'none';
};

/**
 * Method to indicate if the action should write in app cache
 * @param input ActionInput
 */
export const shouldWriteToAppCache = (input: IActionInput, appCache: ICache | null): boolean => {
    return isAppCacheData(input, appCache);
};

/**
 * Method to indicate if the action's cache type is application
 * @param input ActionInput
 */
export const isAppCacheData = (input: IActionInput, appCache: ICache | null): boolean => {
    if (!appCache) {
        return false;
    }

    if (input) {
        if (input.dataCacheType !== undefined && input.dataCacheType() === 'application') {
            return true;
        }

        // if an entity has a cache setting defined, we cache that entity - caching retail proxy actions
        return (<AppCache>appCache).hasCacheSetting(input.getCacheObjectType());
    }

    return false;
};
