        <Module {...HeaderTag}>
            <Node {...HeaderContainer}>
                <div className="wkea-header-state">
                    <Node {...HeaderTopBarContainer}>
                        <div className="wkea-header-state-left">
                            {/* {_renderDesktopAccountBlock(props)}
                            {props.navIcon}
                            <span className="wkea-header-segment">|</span>
                            <a href="/document">帮助中心</a> */}
                            {/* <img src="https://images-us-prod.cms.commerce.dynamics.com/cms/api/qtvnvgqtjn/imageFileData/MA1Qty" alt=""/> */}
