import { NodeTag } from '@msdyn365-commerce-modules/utilities';
import { buildMockModuleProps } from '@msdyn365-commerce/core';
import { render } from 'enzyme';
import * as React from 'react';
import { IAccordionViewProps } from '../../accordion';
import { IAccordionProps } from '../../accordion.props.autogenerated';
import AccordionView from '../../accordion.view';

describe('accordion unit tests - View', () => {
    it('renders correctly', () => {
        const moduleProps: IAccordionProps<{}> = buildMockModuleProps({}, {}) as IAccordionProps<{}>;
        const myReactNodeChild1: React.ReactNode = `whatever child module 1`;
        const myReactNodeChild2: React.ReactNode = `whatever child module 1`;
        moduleProps.slots = {accordionItems: [myReactNodeChild1, myReactNodeChild2]};
        const mockProps: IAccordionViewProps = {
            ...moduleProps,
            heading: '{HeadingComponent}',
              AccordionContainer: {
                moduleProps,
                className: 'ms-accordion'
              },
              HeaderSection: {className: 'ms-accordion-header-section'},
              HeadingWrapper: { className: 'ms-accordion-header-section-container' },
              accordianButtonsContainer: {
                tag: 'div' as NodeTag,
                className: 'ms-accordion-header-section-toggle-button-container',
              },
              accordionItemContainer: {
                tag: 'div' as NodeTag,
                className: 'ms-accordion-item-section',
              },
              accordionButtons: '{accordianButtons}',
              accordionItems : '{accordianButtons}',
        };
        const component = render(<AccordionView {...mockProps} />);
        expect(component).toMatchSnapshot();
    });
    it('render null', () => {
      const moduleProps: IAccordionProps<{}> = buildMockModuleProps({}, {}) as IAccordionProps<{}>;
      const myReactNodeChild1: React.ReactNode = `whatever child module 1`;
      const myReactNodeChild2: React.ReactNode = `whatever child module 1`;
      moduleProps.slots = {accordionItems: [myReactNodeChild1, myReactNodeChild2]};
      const mockProps: IAccordionViewProps = {
          ...moduleProps,
          heading: '',
            AccordionContainer: {
              moduleProps,
              className: 'ms-accordion'
            },
            HeaderSection: {className: 'ms-accordion-header-section'},
            HeadingWrapper: { className: 'ms-accordion-header-section-container' },
            accordianButtonsContainer: {
              tag: 'div' as NodeTag,
              className: 'ms-accordion-header-section-toggle-button-container',
            },
            accordionItemContainer: {
              tag: 'div' as NodeTag,
              className: 'ms-accordion-item-section',
            },
            accordionButtons: '',
            accordionItems : '',
      };
      const component = render(<AccordionView {...mockProps} />);
      expect(component).toMatchSnapshot();
  });
});