import { shallow } from 'enzyme';
import * as React from 'react';

import { ErrorComponent, InfoComponent } from '../../components/account-customer-balance-tile-info';

describe('Account-Customer-Balance-Tile Textual Elements', () => {
    it('should render the Error correctly', () => {
        const mockProps = { error: 'Error message' };
        const component = shallow(<ErrorComponent {...mockProps} />);
        expect(component).toMatchSnapshot();
    });

    it('should render the Info correctly', () => {
        const mockProps = {
            label: 'Customer balance',
            value: '$999.99',
            className: 'test'
        };
        const component = shallow(<InfoComponent {...mockProps} />);
        expect(component).toMatchSnapshot();
    });
});
