import { render } from 'enzyme';
import * as React from 'react';

import { buildMockModuleProps } from '@msdyn365-commerce/core';

import AccountGenericTileModule from '../account-generic-tile';
import {
    HeadingTag, IAccountGenericTileConfig, IAccountGenericTileProps, IHeadingData, ILinksData
} from '../account-generic-tile.props.autogenerated';
import AccountGenericView from '../account-generic-tile.view';

const mockResources = {
    accountAddressTileDescription: 'You have {0} address.'
};

const mockLink1: ILinksData = {
    linkText: 'Mock link 1 title',
    linkUrl: {
        destinationUrl: 'Mock link 1 href'
    },
    ariaLabel: 'Mock link 1 alt text'
};

const mockHeading1: IHeadingData = {
    text: 'Mock heading 1',
    tag: HeadingTag.h1
};

const mockConfigWithHeadingAndLink: IAccountGenericTileConfig = {
    className: 'testClassName',
    heading: mockHeading1,
    links: [mockLink1]
};

const mockConfigWithHeadingAndMultipleLink: IAccountGenericTileConfig = {
    className: 'testClassName',
    heading: mockHeading1,
    links: [mockLink1, mockLink1,]
};

describe('Account-Generic-Tile integration tests', () => {
    it('renders correctly', () => {
        const moduleProps: IAccountGenericTileProps<IAccountGenericTileConfig> = {
            ...(buildMockModuleProps({}, {}, mockConfigWithHeadingAndLink) as IAccountGenericTileProps<IAccountGenericTileConfig>),
            resources: mockResources,
            // @ts-ignore
            renderView: AccountGenericView
        };

        const component = render(<AccountGenericTileModule {...moduleProps} />);
        expect(component).toMatchSnapshot();
    });

    it('renders correctly with multiple links', () => {
        const moduleProps: IAccountGenericTileProps<IAccountGenericTileConfig> = {
            ...(buildMockModuleProps({}, {}, mockConfigWithHeadingAndMultipleLink) as IAccountGenericTileProps<IAccountGenericTileConfig>),
            resources: mockResources,
            // @ts-ignore
            renderView: AccountGenericView
        };

        const component = render(<AccountGenericTileModule {...moduleProps} />);
        expect(component).toMatchSnapshot();
    });
});