import { buildMockRequest } from '@msdyn365-commerce/core';
import { render } from 'enzyme';
import * as React from 'react';

import LinksComponent from '../../components/account-generic-tile-links';

describe('Account-Generic-Tile unit tests - Links', () => {
    it('renders correctly if no links is provideded', () => {
        const mockProps = {
            links: [],
            requestContext: buildMockRequest()
        };
        const component = render(<LinksComponent {...mockProps} />);
        expect(component).toMatchSnapshot();
    });

    it('renders correctly if single links is provideded', () => {
        const mockProps = {
            links: [
                {
                    linkText: 'Link 1',
                    linkUrl: {
                        destinationUrl: '/page1'
                    },
                    ariaLabel: 'Link 1 aria label',
                    openInNewTab: false
                }
            ],
            requestContext: buildMockRequest()
        };
        const component = render(<LinksComponent {...mockProps} />);
        expect(component).toMatchSnapshot();
    });

    it('renders correctly if multiple links is provideded', () => {
        const mockProps = {
            links: [
                {
                    linkText: 'Link 1',
                    linkUrl: {
                        destinationUrl: '/page1'
                    },
                    ariaLabel: 'Link 1 aria label',
                    openInNewTab: false
                },
                {
                    linkText: 'Link 2',
                    linkUrl: {
                        destinationUrl: '/page2'
                    },
                    ariaLabel: 'Link 2 aria label',
                    openInNewTab: true
                }
            ],
            requestContext: buildMockRequest()
        };
        const component = render(<LinksComponent {...mockProps} />);
        expect(component).toMatchSnapshot();
    });

});
