it('Account-Profile unit tests - no data and no config', () => {

    const mockDataWithNoAttributes: IAccountProfileData = {
        customerInformation: RetailActions.wrapInResolvedAsyncResult({
            Email: 'test@test.com',
            FirstName: 'First',
            LastName: 'Last',
            Phone: undefined,
            VatNumber: undefined,
            Attributes: undefined
        } as Customer),
        attributeDefinitions: RetailActions.wrapInResolvedAsyncResult([{RecordId: 0}])
    };

    const mockConfigWithNoHeadings: IAccountProfileConfig = {
        heading: {
            text: '',
            tag: undefined
        },
        nameSectionHeading: {
            text: '',
            tag: undefined
        },
        emailAddressSectionHeading: {
            text: '',
            tag: undefined
        },
        preferencesSectionHeading: {
            text: '',
            tag: undefined
        },
        personalizationSectionHeading: {
            text: '',
            tag: undefined
        },
        webTrackingSectionHeading: {
            text: '',
            tag: undefined
        },
        className: 'test-class',
        additionalInformationSectionHeading: { tag: AdditionalInformationSectionHeadingTag.h2, text: '' },
        showPhone: true,
        phoneSectionHeading: { tag: PhoneSectionHeadingTag.h3, text: '' },
        showVatNumber: true,
        vatNumberSectionHeading: { tag: VatNumberSectionHeadingTag.h3, text: '' },
        showAttributes: undefined
    };

    // @ts-ignore mock partial data
    const moduleProps: IAccountProfileAttributesProps = {
        accountProfileProps: {
            config: mockConfigWithNoHeadings,
            data: mockDataWithNoAttributes,
            context: mockContext,
            ...mockProps
        }
    };
    const wrapper = mount(<AccountProfileAttributes {...moduleProps} />);
    const accountProfileAttributes = wrapper.instance() as AccountProfileAttributes;
    // tslint:disable: no-any
    (accountProfileAttributes as any).gotException  = true;
    expect(accountProfileAttributes.state.editAttributes).toBe(false);
    wrapper.unmount();
});