import { render } from 'enzyme';
import * as React from 'react';

import { wrapInResolvedAsyncResult } from '@msdyn365-commerce-modules/retail-actions';
import { buildMockModuleProps } from '@msdyn365-commerce/core';
import { CommerceList } from '@msdyn365-commerce/retail-proxy';

import AccountWishlistTileModule from '../account-wishlist-tile';
import { IAccountWishlistTileData } from '../account-wishlist-tile.data';
import {
    HeadingTag, IAccountWishlistTileConfig, IAccountWishlistTileProps, IHeadingData, ILinksData
} from '../account-wishlist-tile.props.autogenerated';
import AccountWishlistTileView from '../account-wishlist-tile.view';

const mockResources = {
    accountWishlistTileDescription: 'You have {0} items in the Wishlist.'
};

const mockLink1: ILinksData = {
    linkText: 'Mock link 1 title',
    linkUrl: {
        destinationUrl: 'Mock link 1 href'
    },
    ariaLabel: 'Mock link 1 alt text'
};

const mockHeading1: IHeadingData = {
    text: 'Mock heading 1',
    tag: HeadingTag.h1
};

const mockConfigWithHeadingAndLink: IAccountWishlistTileConfig = {
    className: 'testClassName',
    heading: mockHeading1,
    links: [mockLink1]
};

const mockConfigWithHeadingAndMultipleLink: IAccountWishlistTileConfig = {
    className: 'testClassName',
    heading: mockHeading1,
    links: [mockLink1, mockLink1,]
};

const mockWishlistData: IAccountWishlistTileData = {
    wishlists: wrapInResolvedAsyncResult([{}] as CommerceList[])
};

describe('Account-Wishlist-Tile integration tests', () => {
    it('renders correctly', () => {
        const moduleProps: IAccountWishlistTileProps<IAccountWishlistTileData> = {
            ...(buildMockModuleProps(mockWishlistData, {}, mockConfigWithHeadingAndLink) as IAccountWishlistTileProps<IAccountWishlistTileData>),
            resources: mockResources,
            // @ts-ignore
            renderView: AccountWishlistTileView
        };

        const component = render(<AccountWishlistTileModule {...moduleProps} />);
        expect(component).toMatchSnapshot();
    });

    it('renders correctly with multiple links', () => {
        const moduleProps: IAccountWishlistTileProps<IAccountWishlistTileData> = {
            ...(buildMockModuleProps(mockWishlistData, {}, mockConfigWithHeadingAndMultipleLink) as IAccountWishlistTileProps<IAccountWishlistTileData>),
            resources: mockResources,
            // @ts-ignore
            renderView: AccountWishlistTileView
        };

        const component = render(<AccountWishlistTileModule {...moduleProps} />);
        expect(component).toMatchSnapshot();
    });
});