import { shallow } from 'enzyme';
import * as React from 'react';

import { wrapInResolvedAsyncResult } from '@msdyn365-commerce-modules/retail-actions';
import { buildMockRequest, IRequestContext } from '@msdyn365-commerce/core';
import { CommerceList } from '@msdyn365-commerce/retail-proxy';

import AccountWishlistTileModule from '../../account-wishlist-tile';
import { IAccountWishlistTileData } from '../../account-wishlist-tile.data';
import {
    HeadingTag, IAccountWishlistTileConfig, IAccountWishlistTileProps, IHeadingData, ILinksData
} from '../../account-wishlist-tile.props.autogenerated';

const mockResources = {
    accountAddressDescription: 'You have {0} address.'
};

const mockLink1: ILinksData = {
    linkText: 'Mock link 1 title',
    linkUrl: {
        destinationUrl: 'Mock link 1 href'
    },
    ariaLabel: 'Mock link 1 alt text'
};

const mockHeading1: IHeadingData = {
    text: 'Mock heading 1',
    tag: HeadingTag.h1
};

const mockConfigWithHeadingAndLink: IAccountWishlistTileConfig = {
    className: 'testClassName',
    heading: mockHeading1,
    links: [mockLink1]
};

const mockConfigWithHeading: IAccountWishlistTileConfig = {
    className: 'testClassName',
    heading: mockHeading1,
    links: []
};

const mockWishlistEmptyData: IAccountWishlistTileData = {
    wishlists: wrapInResolvedAsyncResult([] as CommerceList[])
};

const mockWishlistData: IAccountWishlistTileData = {
    wishlists: wrapInResolvedAsyncResult([{ CommerceListLines: [{}] }] as CommerceList[])
};

const mockRequest: IRequestContext = buildMockRequest();
// @ts-ignore
const mockContext: ICoreContext = {
    request: mockRequest,
    app: {
        config: {
            hideRating: false
        },
    },
    // @ts-ignore: Using partial for testing
    cultureFormatter: {
        formatCurrency: jest.fn(price => `$${price}`)
    }
};

const mockProps = {
    resources: mockResources,
    renderView: jest.fn(),
    renderModuleAttributes: jest.fn()
};

describe('Account-Wishlist-Tile unit tests - Container', () => {
    it('renders correctly if all props is provided ', () => {
        // @ts-ignore mock partial data
        const moduleProps: IAccountWishlistTileProps<IAccountWishlistTileData> = {
            config: mockConfigWithHeadingAndLink,
            data: mockWishlistData,
            context: mockContext,
            ...mockProps
        };
        shallow(<AccountWishlistTileModule {...moduleProps} />);
        expect(moduleProps.renderView).toMatchSnapshot();
    });

    it('renders correctly if address has no records', () => {
        // @ts-ignore mock partial data
        const moduleProps: IAccountWishlistTileProps<IAccountWishlistTileData> = {
            config: mockConfigWithHeadingAndLink,
            data: mockWishlistEmptyData,
            context: mockContext,
            ...mockProps
        };
        shallow(<AccountWishlistTileModule {...moduleProps} />);
        expect(moduleProps.renderView).toMatchSnapshot();
    });

    it('renders correctly if links collection is empty', () => {
        // @ts-ignore mock partial data
        const moduleProps: IAccountWishlistTileProps<IAccountWishlistTileData> = {
            config: mockConfigWithHeading,
            data: mockWishlistData,
            context: mockContext,
            ...mockProps
        };
        shallow(<AccountWishlistTileModule {...moduleProps} />);
        expect(moduleProps.renderView).toMatchSnapshot();
    });
});
