export interface ITest {
    testName: string;
    pageUrl: string;
    enabled: boolean;
    category: string;
    env: string;
    testPage: string;
    timeout: number;
    asserts: (IAssertsEntity)[];
    baseFolderPath: string;
  }

export interface IAssertsEntity {
    eventName?: string;
    selector: string;
    existandvisible?: boolean | null;
    wait: number | 500;
    inputText: string | '';
    containsText: string | '';
    urlText: string | '';
    page: string;
    moduleName: string;
    errorMessage: string | '';
  }