// tslint:disable-next-line:no-unused-variable
import { render } from 'enzyme';
import * as React from 'react';
import ErrorTitleComponent from '../../components/checkout-delivery-options.error-title';

describe('CheckoutDeliveryOptions unit tests - Error message', () => {
    it('renders correctly with error message', () => {
        const mockProps = {
            title: 'title'
        };
        const component = render(<ErrorTitleComponent {...mockProps} />);
        expect(component).toMatchSnapshot();
    });

    it('renders correctly with error message not provided', () => {
        const mockProps = {
        };
        const component = render(<ErrorTitleComponent {...mockProps} />);
        expect(component).toMatchSnapshot();
    });
});
