// tslint:disable-next-line:no-unused-variable
import { render } from 'enzyme';
import * as React from 'react';
import ErrorMessageComponent from '../../components/checkout-delivery-options.error-message';

describe('CheckoutDeliveryOptions unit tests - Error title', () => {
    it('renders correctly with error title', () => {
        const mockProps = {
            message: 'message'
        };
        const component = render(<ErrorMessageComponent {...mockProps} />);
        expect(component).toMatchSnapshot();
    });

    it('renders correctly with error title not provided', () => {
        const mockProps = {
        };
        const component = render(<ErrorMessageComponent {...mockProps} />);
        expect(component).toMatchSnapshot();
    });
});
