// tslint:disable-next-line:no-unused-variable
import { render, shallow } from 'enzyme';
import * as React from 'react';

import RadioButtonComponent from '../../components/checkout-delivery-options.radio-button';

describe('CheckoutDeliveryOptions unit tests - RadioButton', () => {
    it('renders correctly', () => {
        const mockProps = {
            isChecked: true,
            value: 'test',
            ariaSetSize: 2,
            ariaPosInSet: 1,
            onChange: jest.fn()
        };
        const component = render(<RadioButtonComponent {...mockProps} />);
        expect(component).toMatchSnapshot();
    });

    it('renders correctly if value not provided', () => {
        const mockProps = {
            isChecked: true,
            ariaSetSize: 2,
            ariaPosInSet: 1,
            onChange: jest.fn()
        };
        const component = render(<RadioButtonComponent {...mockProps} />);
        expect(component).toMatchSnapshot();
    });

    it('call onChange method on click event ', () => {
        const mockProps = {
            isChecked: true,
            value: 'test',
            ariaSetSize: 2,
            ariaPosInSet: 1,
            onChange: jest.fn()
        };
        const component = shallow(<RadioButtonComponent {...mockProps} />);
        const input = component.find('input').at(0);
        input.simulate('change');
        expect(mockProps.onChange).toBeCalled();
    });
});
