// tslint:disable-next-line:no-unused-variable
import getAddContactInfo from '../../components/get-add-contact-info';

const mockData = {
    hasError: false,
    email: 'abc@example.com',
    resources: {
        emailLabel: 'Email address for order questions',
        emailErrortext: 'Please enter a valid email',
        saveBtnLabel: 'Save',
        cancelBtnLabel: 'Cancel'
    },
    canSubmit: true,
    canCancel: true,
    onChange: jest.fn(),
    onSubmit: jest.fn(),
    onCancel: jest.fn()
};

describe('CheckoutGuestProfile unit tests - AddContactInfo', () => {
    it('renders correctly', () => {
        const mockProps = {
            ...mockData,
            hasError: false
        };
        const obj = getAddContactInfo(mockProps);
        expect(obj).toMatchSnapshot();
    });

    it('renders correctly with error', () => {
        const mockProps = {
            ...mockData,
            hasError: true
        };
        const obj = getAddContactInfo(mockProps);
        expect(obj.error).toMatchSnapshot();
    });
});
