// tslint:disable-next-line:no-unused-variable
import { buildMockModuleProps } from '@msdyn365-commerce/core';
import { render } from 'enzyme';
import * as React from 'react';
import { ICheckoutGuestProfileModuleProps } from '../../checkout-guest-profile';
import CheckoutGuestProfileView from '../../checkout-guest-profile.view';

const moduleProps: ICheckoutGuestProfileModuleProps = buildMockModuleProps({}, {}) as ICheckoutGuestProfileModuleProps;

describe('CheckoutGuestProfile unit tests - View', () => {
    it('renders correctly ', () => {
        const mockProps = {
            ...moduleProps,
            email: 'mock@email.com',
            moduleProps: { moduleProps, className: 'module-class' },
            showContactInfo: {
                showInfoProps: { className: 'show-info-class' },
                email: '{email}',
                editButton: '{editButton}'
            },
            addContactInfo: undefined,
            className: 'mock-class'
        };
        const component = render(<CheckoutGuestProfileView {...mockProps} />);
        expect(component).toMatchSnapshot();
    });

    it('renders correctly ', () => {
        const mockProps = {
            ...moduleProps,
            email: 'mock@email.com',
            moduleProps: { moduleProps: moduleProps, className: 'module-class' },
            showContactInfo: undefined,
            addContactInfo: {
                addFormProps: { className: 'add-form-class' },
                submitButton: '{submitButton}',
                cancelButton: '{cancelButton}',
                inputGroupProps: { className: 'input-group-class' },
                label: '{label}',
                error: '{error}',
                input: '{input}'
            },
            className: 'mock-class'
        };
        const component = render(<CheckoutGuestProfileView {...mockProps} />);
        expect(component).toMatchSnapshot();
    });
});
