// tslint:disable-next-line:no-unused-variable
import { shallow } from 'enzyme';
import * as React from 'react';
import AddPaymentFormComponent, { IAddPaymentFormProps } from '../../components/add-payment-form';
import { mockResources } from '../__mock__';

describe('Checkout payment instrument unit tests - Add payment form', () => {
    it('renders correctly', () => {
        const mockProps: IAddPaymentFormProps = {
            acceptPageUrl: '/acceptPageUrl',
            acceptPageContent: '<html>Mock DOM</html>',
            iFrameHeightOverride: 500,
            requestUrlOrigin: '/serverPageUrl',
            isFetchingPaymentConnector: false,
            paymentStyleOverride: '.body {}',
            resources: mockResources
        };
        const component = shallow(<AddPaymentFormComponent {...mockProps} />);
        expect(component).toMatchSnapshot();
    });
});
