// tslint:disable-next-line:no-unused-variable
import { buildMockModuleProps } from '@msdyn365-commerce/core';
import { shallow } from 'enzyme';
import * as React from 'react';
import { ICheckoutPaymentInstrumentProps } from '../../checkout-payment-instrument.props.autogenerated';
import ViewComponent from '../../checkout-payment-instrument.view';

describe('Checkout payment instrument unit tests - View', () => {
    const moduleProps: ICheckoutPaymentInstrumentProps<{}> = buildMockModuleProps({}, {}) as ICheckoutPaymentInstrumentProps<{}>;
    it('renders correctly', () => {
        const mockProps = {
            ...moduleProps,
            checkoutPaymentInstrument: { moduleProps, className: 'mock-module' },
            className: 'mock-class',
            waiting: '{waiting}',
            error: '{error}',
            paymentInformation: '{paymentInformation}',
            addPaymentForm: '{addPaymentForm}'
        };
        const component = shallow(<ViewComponent {...mockProps} />);
        expect(component).toMatchSnapshot();
    });
});
