// tslint:disable-next-line:no-unused-variable
import { render, shallow } from 'enzyme';
import * as React from 'react';

import CheckBoxComponent from '../../components/checkout-terms-and-conditions.check-box';

describe('CheckoutTermsAndConditions - check-box', () => {
    it('renders correctly', () => {
        const mockProps = {
            isChecked: true,
            value: 'test',
            disabled: false,
            onChange: jest.fn()
        };
        const component = render(<CheckBoxComponent {...mockProps} />);
        expect(component).toMatchSnapshot();
    });

    it('renders correctly if value not provided', () => {
        const mockProps = {
            isChecked: true,
            disabled: true,
            onChange: jest.fn()
        };
        const component = render(<CheckBoxComponent {...mockProps} />);
        expect(component).toMatchSnapshot();
    });

    it('call onChange method on click event ', () => {
        const mockProps = {
            isChecked: true,
            value: 'test',
            disabled: true,
            onChange: jest.fn()
        };
        const component = shallow(<CheckBoxComponent {...mockProps} />);
        const input = component.find('input').at(0);
        input.simulate('change');
        expect(mockProps.onChange).toBeCalled();
    });
});
