// tslint:disable-next-line:no-unused-variable
import { render } from 'enzyme';
import * as React from 'react';
import ErrorMessage from '../../components/checkout-terms-and-conditions.error-message';

describe('CheckoutTermsAndConditions - Error message', () => {
    it('renders correctly with error message', () => {
        const mockProps = {
            message: 'message'
        };
        const component = render(<ErrorMessage {...mockProps} />);
        expect(component).toMatchSnapshot();
    });

    it('renders correctly with error message not provided', () => {
        const mockProps = {
        };
        const component = render(<ErrorMessage {...mockProps} />);
        expect(component).toMatchSnapshot();
    });
});
