// tslint:disable-next-line:no-unused-variable
import { ITelemetryContent } from '@msdyn365-commerce-modules/utilities';
import { buildMockRequest } from '@msdyn365-commerce/core';
import { render } from 'enzyme';
import * as React from 'react';
import LinksComponent from '../../components/links';

describe('Content card unit tests - Link', () => {
    it('renders correctly with one link', () => {
        const mockProps = {
            links: [
                {
                    linkText: 'Shop now',
                    linkUrl: {
                        destinationUrl: '#'
                    },
                    ariaLabel: 'shop now',
                    openInNewTab: false
                }
            ],
            requestContext: buildMockRequest(),
            telemetryContent:{} as ITelemetryContent,
        };
        const component = render(<LinksComponent {...mockProps} />);
        expect(component).toMatchSnapshot();
    });

    it('renders correctly with multiple links', () => {
        const mockProps = {
            links: [
                {
                    linkText: 'Shop now',
                    linkUrl: {
                        destinationUrl: '#'
                    },
                    ariaLabel: 'shop now',
                    openInNewTab: false
                },
                {
                    linkText: 'Learn more',
                    linkUrl: {
                        destinationUrl: '#'
                    },
                    ariaLabel: 'Learn more',
                    openInNewTab: true
                }
            ],
            requestContext: buildMockRequest(),
            telemetryContent:{} as ITelemetryContent,
        };
        const component = render(<LinksComponent {...mockProps} />);
        expect(component).toMatchSnapshot();
    });
});
