/** This represents a heading.
 */
// tslint:disable-next-line: interface-name
export interface Heading {
    /* Heading Text */
    headingText: string;

    /* Heading Tag */
    tag?: HeadingTag;

    /* Heading Size */
    textSize?: TextSize;
}

export type HeadingTag = 'h1' | 'h2' | 'h3' | 'h4' | 'h5' | 'h6';

export type TextSize = 'sm' | 'md' | 'lg' | 'xl';
