import { SimpleProduct } from '@msdyn365-commerce/retail-proxy';
import { Image } from './Image';
/**
 * This represents a list data type.
 * @typeName listIdentityReference
 */
export interface List {
    Title?: string;
    Description?: string;
    ShortDescription?: string;
    BackgroundColor?: string;
    ForegroundColor?: string;
    Items: Array<SimpleProduct | ISubList>;
    ItemsCount: number;
}
export interface ISubList {
    Title?: string;
    Description?: string;
    ShortDescription?: string;
    BackgroundColor?: string;
    ForegroundColor?: string;
    Href?: string;
    Images?: Image[];
}
