/** This represents a badge.
 * @typeName badge
 */
export interface Badge {

    /* Text on the badge*/
    text: string,

    /* Type of the badge*/
    badgeType?: BadgeType,

    /* Size of the badge*/
    badgeSize?: BadgeSize
}

export type BadgeType = 'highlight' | 'lowlight' | 'accent' | 'none';

export type BadgeSize = 'small' | 'large' | 'responsive';