/** This represents an Image.
 * @typeName imageIdentityReference
 */
export interface Image {
    /* Image url */
    href: string,

    /* Alt text for image */
    altText?: string,

    /* Image caption */
    caption?: string,

    /* Image tittle */
    title?: string,

    /* Image width */
    width?: number,

    /* Image height */
    height?: number,

    /* Image focal point */
    focalRegion?: FocalRegion,

    /* Image crop regions */
    cropRegions?: CropRegion
}

/**
 * cms datatype for focal region
 */
export interface FocalRegion {
    x1?: number;
    y1?: number;
    x2?: number;
    y2?: number;
}

/**
 * cms datatype for crop region
 */

export interface CropRegion {
    top?: number;
    left?: number;
    width?: number;
    height?: number;
    targetWidth?: number;
    targetHeight?: number;
}
