/** This represents a link.
 * @typeName actionLink
 */
export interface Link {

     /* Actual destination link */
    href: string,

    /* Clicakble text */
    text: string,

    /* ARIA lebel for the anchor */
    ariaLabel?: string

    /* Type of the link which determines the behaviour */
    linkType?: LinkType
}

export type LinkType = 'callToAction' | 'download' | 'edit' | 'phone' | 'chat' | 'addToCart';
