import {Image} from './Image'

/** This represents a logo.
 * @typeName logo
 */
export interface Logo {

    /* Url associated with the logo image*/
    destinationUrl?: string,

    /* Actual  logo image*/
    image: Image,

    /* Shape of the logo image*/
    IconType?: IconType
}

export type IconType  = 'square' | 'rectangle' | 'round' | 'largeRectangle';


