/** This represents a simple external link.
 * @typeName simpleLink
 */
export interface SimpleLink {

    /* Actual destination link */
    destinationUrl: string,

    /* Clicakble text */
    text?: string,

    /* ARIA lebel for the anchor */
    ariaLabel?: string

    /* Type of the link which determines the behaviour */
    openInNewTab?: boolean
}


