import { Image } from './Image';

/** This represents a video.
 * @typeName videoIdentityReference
 */
export interface Video {
    _links?:IVideoFileDataLinks;
    playtime?: number;
    subtitle?: string;
    thumbnail?: Image;
    title?: string;
    description?: string;
    interactiveTriggersEnabled?: boolean;
    minimumAge?: number;
    _id?: string;
    _locale?: string;
}

/**
 * cms datatype of VideoFileDataLinks
 */
export interface IVideoFileDataLinks {
    binaryReferences?: IBinaryReference[];
}

/**
 * cms datatype of BinaryReference
 */
export interface IBinaryReference {
    format?: string;
    bitrate?: number;
    clientHref?: string;
    height?: number;
    width?: number;
    alias?: string;
    contentType?: string;
    description?: string;
    extension?: string;
    sizeInBytes?: number;
    sourceHref?: string;
    href?: string;
    locale?: string;
    $type?: string;
}
