import { buildMockModuleProps} from '@msdyn365-commerce/core';
/// <reference types="jest" />

// tslint:disable-next-line:no-unused-variable
import * as React from 'react';
import * as renderer from 'react-test-renderer';

import BackTopFooter from '../back-top-footer';
import {
  IBackTopFooterConfig,
  IBackTopFooterProps
} from '../back-top-footer.props.autogenerated';

const mockData = {
    myText: {text: 'simple text'}
};

const mockConfig: IBackTopFooterConfig = {
  ariaLabel: 'back to top',
  backtoTopText: 'top',
  destination: '#renderPage'
};

const mockConfigWithoutDestination: IBackTopFooterConfig = {
  ariaLabel: 'back to top',
  backtoTopText: 'top',
};

const mockActions = {};

describe('BackTopFooter', () => {
  let moduleProps: IBackTopFooterProps<{}>;
  beforeAll(() => {
    moduleProps = buildMockModuleProps(mockData, mockActions, mockConfig) as IBackTopFooterProps<{}>;
  });
  it('renders correctly', () => {
    const component: renderer.ReactTestRenderer = renderer.create(
      <BackTopFooter {...moduleProps} />
    );
    const tree = component.toJSON();
    expect(tree).toMatchSnapshot();
  });

  it('renders correctly without destination', () => {
    const modulePropsWithoutDest = buildMockModuleProps(mockData, mockActions, mockConfigWithoutDestination) as IBackTopFooterProps<{}>;
    const component: renderer.ReactTestRenderer = renderer.create(
      <BackTopFooter {...modulePropsWithoutDest} />
    );
    const tree = component.toJSON();
    expect(tree).toMatchSnapshot();
  });
});
