import { buildMockModuleProps} from '@msdyn365-commerce/core';
/// <reference types="jest" />

// tslint:disable-next-line:no-unused-variable
import * as React from 'react';
import * as renderer from 'react-test-renderer';

import FooterCategory from '../../footer-category';
import {
  IFooterCategoryConfig,
  IFooterCategoryProps
} from '../../footer-category.props.autogenerated';

const mockConfig: IFooterCategoryConfig = {
  categoryClassName: 'footer-category'
};

const mockActions = {};

describe('FooterCategory', () => {
  let moduleProps: IFooterCategoryProps<IFooterCategoryConfig>;
  beforeAll(() => {
    moduleProps = buildMockModuleProps({}, mockActions, mockConfig) as IFooterCategoryProps<IFooterCategoryConfig>;
  });
  it('renders correctly', () => {
    const component: renderer.ReactTestRenderer = renderer.create(
      <FooterCategory {...moduleProps} />
    );
    const tree = component.toJSON();
    expect(tree).toMatchSnapshot();
  });
});
