import { buildMockModuleProps } from '@msdyn365-commerce/core';
import { render } from 'enzyme';
import * as React from 'react';
import { IFooterCategoryViewProps } from '../../footer-category';
import { IFooterCategoryConfig, IFooterCategoryProps } from '../../footer-category.props.autogenerated';
import { FooterCategoryView } from '../../footer-category.view';

const moduleProps = buildMockModuleProps({}, {}, []) as IFooterCategoryProps<IFooterCategoryConfig>;

describe('footer category view tests', () => {
    it('renders correctly', () => {
        const mockCategories: React.ReactNode[] = ['category1', 'category2'];
        // @ts-ignore
        const mockProps: IFooterCategoryViewProps = {
            categories: mockCategories,
            FooterList: { moduleProps, className: 'module-class-FooterList' },
            FooterListItem: { className: 'module-class-FooterListItem' }
        };

        const view = render(<FooterCategoryView {...mockProps}/>);
        expect(view).toMatchSnapshot();
    });

    it('doesnt render when categories is invalid', () => {
         // @ts-ignore
        const mockProps: IFooterCategoryViewProps = {
            FooterList: { moduleProps, className: 'mock-class-FooterList' },
            FooterListItem: { className: 'mock-class-FooterListItem' },
            ...moduleProps
        };
        const view = render(<FooterCategoryView {...mockProps}/>);
        expect(view).toMatchSnapshot();
    });
});