import { buildMockModuleProps } from '@msdyn365-commerce/core-internal';
/// <reference types="jest" />

// tslint:disable-next-line:no-unused-variable
import * as React from 'react';
import * as renderer from 'react-test-renderer';

import { IStoreSelectorStateManager } from '@msdyn365-commerce-modules/bopis-utilities';
import { AsyncResult } from '@msdyn365-commerce/core';
import Map from '../map';
import { IMapData } from '../map.data';
import {
  IMapConfig,
  IMapProps
} from '../map.props.autogenerated';

const mockData: IMapData = {
  actionResponse: {
    text: 'Sample Response Data'
  },
  storeSelectorStateManager: {
    status: 'SUCCESS',
    result: {}
  } as AsyncResult<IStoreSelectorStateManager>,
};

const mockConfig: IMapConfig = {
  heading: {
    text: 'Map'
  },
  className: 'testClassName'
};

const mockActions = {};

describe('StoreLocator', () => {
  let moduleProps: IMapProps<IMapData>;
  beforeAll(() => {
    moduleProps = buildMockModuleProps(mockData, mockActions, mockConfig) as IMapProps<IMapData>;
  });
  it('renders correctly', () => {
    const component: renderer.ReactTestRenderer = renderer.create(
      <Map {...moduleProps} />
    );
    const tree = component.toJSON();
    expect(tree).toMatchSnapshot();
  });
});
