import { render } from 'enzyme';
import * as React from 'react';
import {
    OrderTemplateListStatusMessage
} from '../../components';
import { orderTemplateListStatus } from '../../order-template-list';

describe('OrderTemplateList- Unit Tests', () => {

    it('OrderTemplateListStatusMessage - LOADING - renders correctly', () => {
        const mockPropsMessage = {
            errorType: 'LOADING' as orderTemplateListStatus,
            text: 'Loading...'
        };

        const component = render(<OrderTemplateListStatusMessage {...mockPropsMessage} />);
        expect(component).toMatchSnapshot();
    });

    it('OrderTemplateListStatusMessage - EMPTY - renders correctly', () => {
        const mockPropsMessage = {
            errorType: 'EMPTY' as orderTemplateListStatus,
            text: 'Your order template lines is empty.'
        };

        const component = render(<OrderTemplateListStatusMessage {...mockPropsMessage} />);
        expect(component).toMatchSnapshot();
    });
});
