// tslint:disable-next-line:no-unused-variable
import { render, shallow } from 'enzyme';
import * as React from 'react';
import { PromoBannerCloseButton } from '../../components';

describe('PromoBanner unit tests - Close button', () => {
    it('renders correctly', () => {
        const mockProps = {
            closeButtonAriaLabel: 'Close promo-banner',
            label: 'Close',
            onClose: jest.fn()
        };
        const component = render(<PromoBannerCloseButton {...mockProps} />);
        expect(component).toMatchSnapshot();
    });

    it('calls onClose after clicking the close button', () => {
        const mockProps = {
            resources: {
                closeButtonAriaLabel: 'Close promo-banner',
                closeButtonLabel: 'Close'
            },
            onClose: jest.fn()
        };
        const component = shallow(<PromoBannerCloseButton {...mockProps} />);
        const closeButton = component.find('button').at(0);
        closeButton.simulate('click');
        expect(mockProps.onClose).toBeCalled();
    });
});
