/**
 * User's Review Data sent for submission
 */
export interface IUserReviewData {
    /**
     * The rating of the review
     */
    rating?: number;

    /**
     * The user's name
     */
    userName?: string;

    /**
     * The user's email
     */
    userEmail?: string;

    /**
     * The title of the review
     */
    title?: string;

    /**
     * The review's text
     */
    reviewText?: string;

    /**
     * The name of the product
     */
    productName?: string;

    /**
     * The legal entity
     */
    legalEntity?: string;
}
