import { buildMockModuleProps } from '@msdyn365-commerce/core';
import { render } from 'enzyme';
import * as React from 'react';
import { ISocialShareItemViewProps } from '../../social-share-item';
import { ISocialShareItemConfig, ISocialShareItemProps } from '../../social-share-item.props.autogenerated';
import { SocialShareItemView } from '../../social-share-item.view';

const moduleProps: ISocialShareItemProps<ISocialShareItemConfig> = buildMockModuleProps({}, {}) as ISocialShareItemProps<ISocialShareItemConfig>;

const mockProps: ISocialShareItemViewProps = {
  ...moduleProps,
  SocialShareItem: {
    moduleProps,
    className: 'ms-social-item-content'
  },
  SocialShareItemElement: <div/>,
};

const mockProps1: ISocialShareItemViewProps = {
  ...moduleProps,
  SocialShareItem: {
    moduleProps,
    className: 'ms-social-item-content'
  },
  SocialShareItemElement: null,
};

describe('tab-item unit tests - View', () => {
  it('renders content', () => {
    const component = render(<SocialShareItemView {...mockProps} />);
    expect(component).toMatchSnapshot();
  });

  it('renders null', () => {
    const component = render(<SocialShareItemView {...mockProps1} />);
    expect(component).toMatchSnapshot();
  });
});