import * as React from 'react';

import { buildMockModuleProps } from '@msdyn365-commerce/core';
import { render } from 'enzyme';
import SpacerModule from '../../spacer';
import { ISpacerConfig, ISpacerProps } from '../../spacer.props.autogenerated';
import SpacerView from '../../spacer.view';

const mockConfig: ISpacerConfig = {
    className: 'test-class'
};

describe('Spacer unit tests - Container', () => {
    it('renders correctly with text only', () => {
        const moduleProps = {
            ...buildMockModuleProps({}, {}, mockConfig) as ISpacerProps<{}>,
            // @ts-ignore
            renderView: SpacerView as ISpacerProps
        };

        const module = render(<SpacerModule {...moduleProps} />);
        expect(module).toMatchSnapshot();
    });

    it('renders correctly with no className', () => {
        const moduleProps = {
            ...buildMockModuleProps({}, {}, {}) as ISpacerProps<{}>,
            // @ts-ignore
            renderView: SpacerView as ISpacerProps
        };

        const module = render(<SpacerModule {...moduleProps} />);
        expect(module).toMatchSnapshot();
    });
});
