// tslint:disable-next-line:no-unused-variable
import { buildMockModuleProps } from '@msdyn365-commerce/core';
import { render } from 'enzyme';
import * as React from 'react';
import { ISpacerProps } from '../../spacer.props.autogenerated';
import SpacerView from '../../spacer.view';

describe('Spacer unit tests - View', () => {
    it('renders correctly', () => {
        const moduleProps: ISpacerProps<{}> = buildMockModuleProps({}, {}) as ISpacerProps<{}>;
        const mockProps = {
            ...moduleProps,
            text: '{TextComponent}',
            links: '{LinksComponent}',
            closeButton: '{CloseButonComponent}',
            className: 'custom-class',
            isVisible: true,
            onDismiss: jest.fn()
        };
        const component = render(<SpacerView {...mockProps} />);
        expect(component).toMatchSnapshot();
    });
});
