import { buildMockModuleProps,buildMockRequest,IRequestContext} from '@msdyn365-commerce/core';
import { render } from 'enzyme';
// tslint:disable-next-line:no-unused-variable
import * as React from 'react';
import {ITextBlockConfig, ITextBlockProps, TextBlock, TextBlockView} from '../../..';

const mockConfig: ITextBlockConfig = {
  paragraph: '<p> This is a sample text block</p>'
};
// @ts-ignore
const mockRequest: IRequestContext = buildMockRequest();
// @ts-ignore
const mockContext: ICoreContext = {
    request: mockRequest
};

describe('TextBlock', () => {
  it('renders correctly', () => {
    const moduleProps: ITextBlockProps<{}> = {
        ...(buildMockModuleProps({}, {}, mockConfig, mockContext) as ITextBlockProps<{}>),
        // @ts-ignore
        renderView: TextBlockView
    };
    const component = render(<TextBlock {...moduleProps} />);
    expect(component).toMatchSnapshot();
});
});
