import * as React from 'react';
import { IFadeProps } from '../fade/Fade.props';

/**
 * Alert properties.
 */
export interface IAlertProps extends React.HTMLProps<HTMLElement> {

    /** Flag to toggle collapse open */
    isOpen?: boolean;

    /** CSS class that needs to be applied to the Alert */
    className?: string;

    /** Color of the alert */
    color?: string;

    transition?: IFadeProps;

    closeAriaLabel?: string;

    fade?: boolean;

    cssModule?: object;

    closeClassName?: string;

    /** Default false. If true, alert will be assertive and read out whenver it changes */
    assertive?: boolean;

    /** Default true. If false, won't include any of the msc-alert classes */
    includeAlertClass?: boolean;

    tag?: React.ReactType;

    /** Function to call on pressing close button */
    toggle?():void;
}
