import { render } from 'enzyme';
import * as React from 'react';

import BackToTop from './BackToTop';

describe('back-to-top', () => {
    it('renders default as expected', () => {
        const backToTop = render(<BackToTop href='#app' aria-label='Back to top' />);
        expect(backToTop).toMatchSnapshot();
    });
});

describe('back-to-top', () => {
    it('renders glyph only as expected', () => {
        const backToTop = render(
            <BackToTop href='#app' aria-label='Back to top'>
                <i className='fa fa-chevron-circle-up' />
            </BackToTop>);
        expect(backToTop).toMatchSnapshot();
    });
});

describe('back-to-top', () => {
    it('renders text and glyph as expected', () => {
        const backToTop = render(
            <BackToTop href='#app' aria-label='Back to top'>
                Top<i className='ml-2 fa fa-arrow-up' />
            </BackToTop>);
        expect(backToTop).toMatchSnapshot();
    });
});

describe('back-to-top', () => {
    it('renders text and glyph with threshold as expected', () => {
        const backToTop = render(
            <BackToTop href='#app' aria-label='Back to top' threshold={50}>
                Top<i className='ml-2 fa fa-arrow-up' />
            </BackToTop>);
        expect(backToTop).toMatchSnapshot();
    });
});

describe('back-to-top', () => {
    it('renders text and glyph with slide-in as expected', () => {
        const backToTop = render(
            <BackToTop
                className='btn btn-lg btn-secondary border border-light position-fixed slide-in'
                href='#app'
                aria-label='Back to top'
            >
                Top<i className='ml-2 fa fa-arrow-up' />
            </BackToTop>
        );
        expect(backToTop).toMatchSnapshot();
    });
});

describe('back-to-top', () => {
    it('renders text and glyph as expected with added props delayInMs and scrollStepInPx', () => {
        const backToTop = render(
            <BackToTop
                className='btn btn-lg btn-secondary border border-light position-fixed slide-in'
                href='#app'
                // tslint:disable-next-line:jsx-alignment
                delayInMs={5} scrollStepInPx={50}
                aria-label='Back to top'
            >
                Top<i className='ml-2 fa fa-arrow-up' />
            </BackToTop>
        );
        expect(backToTop).toMatchSnapshot();
    });
});

describe('back-to-top', () => {
    it('renders with tabindex', () => {
        const backToTop = render(
            <BackToTop
                className='btn btn-lg btn-secondary border border-light position-fixed slide-in'
                href='#app'
                // tslint:disable-next-line:jsx-alignment
                delayInMs={5} scrollStepInPx={50}
                aria-label='Back to top'
            >
                Top<i className='ml-2 fa fa-arrow-up' />
            </BackToTop>
        );
        expect(backToTop).toMatchSnapshot();
    });
});