import { render } from 'enzyme';
import * as React from 'react';
import { TransitionTimeouts } from '../common/utilities';
import Collapse from './Collapse';

describe('Collapse', () => {
    it('Rendering the collapse component', () => {
        const collapse = render(
        <Collapse isOpen={true} timeout={TransitionTimeouts.Collapse}>
            <div className='card'>
                This is an example div.
                It will collapse and expand by clicking on 'Toggle' button.
            </div>

        </Collapse>);
        expect(collapse).toMatchSnapshot();
    });
});
