import { render } from 'enzyme';
import * as React from 'react';

import Flipper from './Flipper';

describe('Flipper', () => {
    it('renders correctly', () => {
        const flipper = render(
            <div>
                <Flipper
                    glyphProps={{ className: 'fa fa-chevron-left' }}
                    aria-label='View Previous'
                    displayTooltip={false}
                    id='flipperPreviousButton'
                />
                <Flipper
                    glyphProps={{ className: 'fa fa-chevron-right' }}
                    aria-label='View Next'
                    size={'small'}
                    displayTooltip={false}
                    id='flipperSmallNextButton'
                />
                <Flipper
                    glyphProps={{ className: 'fa fa-chevron-right' }}
                    aria-label='View Next'
                    size={'large'}
                    displayTooltip={false}
                    id='flipperLargeNextButton'
                />
                <Flipper
                        id= {'flipperPreviousButton'}
                        tabIndex={-1}
                        glyphProps={{ className: 'fa fa-chevron-left' }}
                        className='ss-carousel__flipper'
                        aria-label={'View Previous'}
                        displayTooltip={true}
                />
            </div>
        );
        expect(flipper).toMatchSnapshot();
    });
});
