/** Heading Component Properties */
export interface IHeadingProps {
    /* classes */
    className?: string;

    /* Heading Text */
    text: string;

    /* Heading Tag (depreciated) */
    headingTag?: HeadingTag;

    /* Heading Tag */
    tag?: HeadingTag;

    /*
        CMS object type
        TODO - Work around fix to resolve "Invalid attribute name: $type" error. Tacking bug #21153846.
    */
    $type?: string;
}

export type HeadingTag = 'h1' | 'h2' | 'h3' | 'h4' | 'h5' | 'h6';
