import { render } from 'enzyme';
import * as React from 'react';

import Heading from './Heading';
import { IHeadingProps } from './Heading.props';

describe('Heading', () => {
    it('renders correctly with provided headingTag', () => {
        const props: IHeadingProps = {
            text: 'content',
            headingTag: 'h2'
        };
        const wrapper = render(<Heading {...props} />);
        expect(wrapper).toMatchSnapshot();
    });

    it('renders correctly with other props being accidently passed', () => {
        // tslint:disable-next-line:no-any
        const props: any = {
            text: 'content',
            $type: 'heading',
            $foo: 'bar'
        };
        const wrapper = render(<Heading {...props} />);
        expect(wrapper).toMatchSnapshot();
    });

    it('renders correctly with default headingTag', () => {
        const props: IHeadingProps = { text: 'content' };
        const wrapper = render(<Heading {...props} />);
        expect(wrapper).toMatchSnapshot();
    });

    it('renders correctly with provided Tag', () => {
        const props: IHeadingProps = { tag: 'h4', text: 'content' };
        const wrapper = render(<Heading {...props} />);
        expect(wrapper).toMatchSnapshot();
    });
});
