import { mount } from 'enzyme';
import * as React from 'react';
import LabeledDropdown from './LabeledDropdown';
import {LabeledDropdownExample} from './LabeledDropdown.examples';

const options = [
    {key: 'a', value: 'apple'},
    {key: 'b', value: 'banana'},
    {key: 'c', value: 'cucumber'},
    {key: 'delta', value: 'pear'},
    {key: 'e', value: 'tomato'},
];
const spyOnToggle = jest.fn();

describe('Labeled Dropdown', () => {
    it('LabeledDropdownExample',  () => {
        const wrapper = mount(
            <LabeledDropdownExample/>
        );
        expect(wrapper).toMatchSnapshot();
    });

    it('Change method test case.',()=> {
        const  wrapper = mount(
                <LabeledDropdown
                    dropdownOptions={options}
                    selectedOption={options[1]}
                    onSelectOption={spyOnToggle}
                    labelText='fruit'
                    dropdownId='testDropdownId'
                    controlledByParent={true}
                />
            );
        wrapper.find('#testDropdownId').simulate('change');
        expect(spyOnToggle).toBeCalled();
    });
});