import * as React from 'react';

/**
 * Modal body properties.
 */
export default interface IModalBodyProps extends React.HTMLAttributes<HTMLElement> {
    /** Property to set if you want the HTML tag to be something else */
    tag?: React.ReactType;

    /** Property to set any CSS classnames on the modal */
    className?: string;

     /** ID of the modal body */
     id?:string;

    /** Property to set any CSS classModule on the modal */
    cssModule?: object;
}
